$(function(){"use strict";var e=io.connect("http://0.0.0.0");e.on("fileDone",function(e){var t=$("<li />").html(e.name).addClass("muted");e.success||t.removeClass("muted").addClass("text-error");$("#messages ul").append(t)});e.on("done",function(e){var t=$("<h3 />").addClass("text-info").html("Download complete");$("#messages .text-error").length&&t.removeClass("text-info").addClass("text-error").html("Download error");$("#messages").append(t)});$(document).on("click","#links a",function(e){var t=$(this),n=$("#source_url");n.val(t.attr("href"));e.preventDefault()})});